import mongoose from "mongoose";

const userSchema = new mongoose.Schema({
    name: {
        type: String,
        required: true
    },
    email: {
        type: String,
        required: true
    },
    password: {
        type: String,
        required: true
    },
    image: {
        type: String,
        default: "data:image/jpeg;base64,"
    },
    address: {
        addressLine1: {
            type: String,
            //   default: "Not Provided"
        },
        addressLine2: {
            type: String,
            // default: "Not Provided"
        }
    },
    gender: {
        type: String,
        enum: ["Male", "Female", "Other"],
        
    },
    dob: {
        type: Date,
        default: Date.now
    },
    phoneNo: {
        type: String,
        // default: "Not Provided"
    }
});


const userModel = mongoose.model("User", userSchema);

export default userModel;
